mapper.select:
    select 
    
    from order_audit_log
    
	    
			
			and order_number = #{orderNumber,jdbcType=VARCHAR}
		
		
			and order_flag = #{orderFlag,jdbcType=VARCHAR}
		
		
			and audit_status = #{auditStatus,jdbcType=VARCHAR}
		
		
			and create_time >= #{auditTimeStart,jdbcType=VARCHAR}
		
		
			and create_time <= #{auditTimeEnd,jdbcType=VARCHAR}
		
		 order by create_time desc ,order_number desc

    select 
    
    from order_audit_log
    
	    
			and order_number = #{orderNumber,jdbcType=VARCHAR}
		
		
			and order_flag = #{orderFlag,jdbcType=VARCHAR}
		
		
			and audit_status = #{auditStatus,jdbcType=VARCHAR}
		
		 order by create_time desc limit 0,1

    select 
    
    from ((order_audit_log g JOIN order_info f ON g.order_number = f.order_number AND g.order_flag = f.order_flag ) 
     left join (select o.* from invoice_info o join 
     (select v.order_number,v.order_flag,max(v.create_time) as create_time from invoice_info v group by v.order_number,v.order_flag) d 
     on o.order_number=d.order_number and o.order_flag=d.order_flag and o.create_time=d.create_time ) i 
     on i.order_number=g.order_number and i.order_flag=g.order_flag)
     JOIN ( SELECT g.order_number, g.order_flag, max(g.create_time) AS create_time FROM order_audit_log g
     GROUP BY g.order_number, g.order_flag ) k ON g.order_number = k.order_number
     AND g.order_flag = k.order_flag AND g.create_time = k.create_time  
    
	    
			
			and g.order_number = #{orderNumber,jdbcType=VARCHAR}
		
		
			and g.order_flag = #{orderFlag,jdbcType=VARCHAR}
		
		
			and g.audit_status = #{auditStatus,jdbcType=VARCHAR}
		
		
			and g.create_time >= #{auditTimeStart,jdbcType=VARCHAR}
		
		
			and g.create_time <= #{auditTimeEnd,jdbcType=VARCHAR}
		
		 order by g.create_time desc ,g.order_number desc  
		
			limit #{count,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.insert:
insert into order_audit_log (order_audit_log_id, order_number, order_flag, 
  kf_account, audit_user, audit_status, 
  audit_description, remark, create_time
  )
values (#{orderAuditLogId,jdbcType=BIGINT}, #{orderNumber,jdbcType=VARCHAR}, #{orderFlag,jdbcType=VARCHAR}, 
  #{kfAccount,jdbcType=VARCHAR}, #{auditUser,jdbcType=VARCHAR}, #{auditStatus,jdbcType=VARCHAR}, 
  #{auditDescription,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}
  )
