mapper.select:
    select 
    
    from express_info_remove f join order_info o on  f.order_number=o.order_number and f.order_flag=o.order_flag   
    and f.express_id is not null and f.express_id!='' 
    
		
			and f.order_flag = #{orderFlag,jdbcType=VARCHAR}
		
		
			and f.order_number in
			  
			 #{item}  
			 
		
		
			and f.express_id = #{wayBillNumber,jdbcType=VARCHAR}
		
		
			and o.last_time >= #{wayBillTimeStart,jdbcType=VARCHAR}
		
		
			and o.last_time <= #{wayBillTimeEnd,jdbcType=VARCHAR}
		
		
			and o.order_status in 
			  
			 #{item}  
			 
		
		 order by o.last_time,o.start_time
